function uploadFile() {
  var file = document.getElementById("fileInput").files[0];
  var reader = new FileReader();
  reader.onload = function () {
    document.getElementById("originalCSS").value = reader.result;
  };
  reader.readAsText(file);
}
function convertToRTL() {
  let originalCSS = document.getElementById("originalCSS").value;
  if (originalCSS == "") {
    document.getElementById('Msg').innerHTML = '<p class="note wr">Please select a file to convert</p>';navigator.vibrate([110]);
    return;
  }
  let cssProperties = originalCSS.split(";");
  cssProperties.forEach(function (property) {
    if (property.includes("left")) {
      property = property.replace("left", "right");
    } else if (property.includes("right")) {
      property = property.replace("right", "left");
    } else if (property.includes("padding-left")) {
      property = property.replace("padding-left", "padding-right");
    } else if (property.includes("padding-right")) {
      property = property.replace("padding-right", "padding-left");
    } else if (property.includes("margin-left")) {
      property = property.replace("margin-left", "margin-right");
    } else if (property.includes("margin-right")) {
      property = property.replace("margin-right", "margin-left");
    }
    $("#rtlCSS").append(property + ";");
  });
  document.getElementById('Msg').innerHTML = '<p class="note">CSS Converted Successfully from left to right</p>';
};
